package week1.day2;
//Write a logic to print the odd numbers from 1 to 10.

public class FindOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=200;
		for (int i = 1; i <=a; i++) {
			
		
		if (i%2==1) {
			System.out.println("Odd numbers: "+i);
		}
		
	}
	}

}
